y(this);			var quote_hash = jQuery.md5(blockcite.attr("cite") + "|" + blockcite.text());						jQuery.getJSON(quote_hash + ".js", function(json) {				blockcite.before("<div class='quote_context' id='quote_before_" + quote_hash + "'> \					<blockquote class='quote_context'>" + json["before"] + "</blockquote></div>");				blockcite.after("<div class='quote_context' id='quote_after_" + quote_hash + "'> \					<blockquote class='quote_context'>" + json["after"] + "</blockquote></div>");					jQuery("#quote_before_" + quote_hash).hide();				jQuery("#quote_after_" + quote_hash).hide();							});			jQuery(this).before("<div class='quote_arrows' id='context_up_" + quote_hash + "'> \				<a href=\"javascript:toggle_quote('before', 'quote_before_" + quote_hash + "');\">&#9650;</a></div>");			jQuery(this).after("<div class='quote_arrows' id='context_down_" + quote_hash +"'> \				<a href=\"javascript:toggle_quote('after', 'quote_after_" + quote_hash +"');\">&#9660;</a></div>");		}	});</script><?php include("footer.php"); ?>